/**
 * Responsible for the HTML displayed in the browser / Controls the view inside the browser 
 */

// Import React library and the necessary components
import React, { useState, useEffect } from 'react';
import './App.css';

import Header from './components/Header.jsx';
import SearchForm from './components/SearchForm.jsx';
import PropertyList from './components/PropertyList.jsx';
import PropertyDetails from './components/PropertyDetails.jsx';
import Favorites from './components/Favourites.jsx';
import Footer from './components/Footer.jsx';
import propertiesData from './data/properties.json';

function App() {
  const [properties, setProperties] = useState(propertiesData.properties);
  const [filteredProperties, setFilteredProperties] = useState(properties);
  const [showFavorites, setShowFavorites] = useState(false);
  const [favorites, setFavorites] = useState([]);
  const [searchPerformed, setSearchPerformed] = useState(false); //by default the search performed is set to be false 

  const updateFilteredProperties = (filteredProperties) => {
    setFilteredProperties(filteredProperties); 
    // Update the filtered properties state
    setSearchPerformed(true);
    //Set to true when a search is performed 
  };

  // State for property details
  const [selectedProperty, setSelectedProperty] = useState(null);

  // User effect hooks to load and save favourites in localstorage 
  useEffect(() => {
    const savedFavorites = localStorage.getItem('favorites');
    if (savedFavorites) {
      setFavorites(JSON.parse(savedFavorites));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  // Toggle favorite status of a property
  const toggleFavorite = (propertyId) => {
    setFavorites(prev => {
      if (prev.includes(propertyId)) {
        return prev.filter(id => id !== propertyId);
      }
      return [...prev, propertyId];
    });
    console.log(favorites);
  };

  // Handle selecting a property to view details 
  const handlePropertySelect = (property) => {
    setSelectedProperty(property);
  };

  // To handle going back to the search results from the property details
  const handleBackToSearch = () => {
    setSelectedProperty(null);
  };

  // Filter properties to show only the favorites
  const favoriteProperties = properties.filter(property => 
    favorites.includes(property.id)
  );

  // const clearFavorites = () => {
  //   setFavorites([]); //Clear the favorites state
  //   localStorage.removeItem('favorites'); //clear the local storage
  // }

  return (
    <div>
      <Header />
      {!selectedProperty ? (
        <>
          <button 
            className="favorites-toggle"
            onClick={() => setShowFavorites(!showFavorites)}
          >
            {showFavorites ? 'X' : 'Show Favorites'}
          </button>

          <SearchForm onSearch={updateFilteredProperties} />

          {/* Conditionally Render Messages */}
          {!searchPerformed ? (
            <p className="search-results-message">All properties:</p>
          ) 
          : filteredProperties.length > 0 ? (
            <p className="search-results-message">Here are the search results:</p>
          ) : (
            <p className="no-results-message">No properties found. Please try different criteria.</p>
          )}

          <PropertyList
            properties={filteredProperties}
            onPropertySelect={handlePropertySelect}
            favorites={favorites}
            onToggleFavorite={toggleFavorite}
          />

          <Favorites
          show={showFavorites}
          properties={properties.filter(property => favorites.includes(property.id))}
          onPropertySelect={handlePropertySelect}
          onToggleFavorite={toggleFavorite}
        />
        </>

      ) : (
        <PropertyDetails
          property={selectedProperty}
          onBack={handleBackToSearch}
          isFavorite={favorites.includes(selectedProperty.id)}
          onToggleFavorite={() => toggleFavorite(selectedProperty.id)}
        />
      )}

    <Footer />
    </div>

  );
}

export default App;
