/**
 * PropertyList component renders a list of properties with their details and allows users to mark properties as favorites.
*/
import React from 'react'; 

function PropertyList({properties}) {

//   const onPropertySelect = (property) => {
//     alert(`Property selected: ${property.title}`);
//   };

  return (
    <div className="property-list">
      {properties.map(property => (
        <div
          key={property.id}
          className="property-card"
          draggable
          onDragStart={(e) => {
            e.dataTransfer.setData('propertyId', property.id.toString());
          }}
        >
          <img
            src={`${property.picture}`}
            alt={property.title}
            className="property-image"
          />
          
          <div className="property-info">
            <h3 className="property-title">{property.title}</h3>
            <p className="property-price"> ¬£ {property.price}</p>
            <p className="property-details">
              {property.bedrooms} bedrooms ‚Ä¢ {property.type}
            </p>
            <p className="property-details">{property.shortDescription}</p>
            <button
              onClick={() => toggleFavorite(property.id)}
              className="favorite-button"
            >
              {favorites.includes(property.id) ? '‚ù§Ô∏è' : 'ü§ç'}
            </button>

            <button> View Details</button>
              {/* <button onClick={() => onPropertySelect(property)}className="view-details-button">View Details</button> */}
          </div>
        </div>
      ))}
    </div>
  );
}

export default PropertyList;
