import React, { useState } from 'react';

export const PropertyDetails = ({
  property,
  onBack,
  isFavorite,
  onToggleFavorite,
}) => {
  const [activeTab, setActiveTab] = useState('description');
  const [selectedImage, setSelectedImage] = useState(0);

  if (!property) return null;

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-GB', {
      style: 'currency',
      currency: 'GBP',
      maximumFractionDigits: 0,
    }).format(price);
  };

  return (
    <div className="property-details-page">
      <button onClick={onBack} className="back-button">
        ‚Üê Back to Search
      </button>

      <div className="image-gallery">
        <img
          src={`${property.images[selectedImage]}?w=1200&q=80`}
          alt={property.title}
          className="main-image"
        />
        <div className="thumbnails">
          {property.images.map((image, index) => (
            <img
              key={index}
              src={`${image}?w=200&q=80`}
              alt={`${property.title} - view ${index + 1}`}
              className="thumbnail"
              onClick={() => setSelectedImage(index)}
            />
          ))}
        </div>
      </div>

      <h1>{property.title}</h1>
      <p className="property-price">{formatPrice(property.price)}</p>
      <button onClick={onToggleFavorite}>
        {isFavorite ? '‚ù§Ô∏è Remove from Favorites' : 'ü§ç Add to Favorites'}
      </button>

      <div className="tabs">
        <div className="tab-list">
          <button
            className={`tab ${activeTab === 'description' ? 'active' : ''}`}
            onClick={() => setActiveTab('description')}
          >
            Description
          </button>
          <button
            className={`tab ${activeTab === 'floorplan' ? 'active' : ''}`}
            onClick={() => setActiveTab('floorplan')}
          >
            Floor Plan
          </button>
          <button
            className={`tab ${activeTab === 'map' ? 'active' : ''}`}
            onClick={() => setActiveTab('map')}
          >
            Map
          </button>
        </div>

        <div className="tab-content">
          {activeTab === 'description' && (
            <div>
              <p>{property.longDescription}</p>
              <h3>Key Features</h3>
              <ul>
                <li>{property.bedrooms} bedrooms</li>
                <li>{property.type}</li>
                <li>Prime location</li>
              </ul>
            </div>
          )}
          {activeTab === 'floorplan' && (
            <div>
              <p>Floor plan coming soon...</p>
            </div>
          )}
          {activeTab === 'map' && (
            <div className="map-container">
              <iframe
                width="100%"
                height="100%"
                frameBorder="0"
                style={{ border: 0 }}
                src={`https://www.google.com/maps/embed?q=${property.location.lat},${property.location.lng}`}
                allowFullScreen
              ></iframe>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
