import React from 'react';

const PropertyList = ({ properties, onPropertySelect, favorites, onToggleFavorite }) => {
  return (
    <div className="property-list">
      {properties.map((property) => (
        <div
          key={property.id}
          className="property-card"
          onClick={() => onPropertySelect(property)}
        >
          <img
            src={property.imageUrl}
            alt={property.title}
            className="property-image"
          />
          <div className="property-info">
            <h3 className="property-title">{property.title}</h3>
            <p className="property-price">${property.price}</p>
            <p className="property-details">{property.bedrooms} Bedrooms</p>
            <p className="property-details">Postcode: {property.postcode}</p>
            <button
              onClick={(e) => {
                e.stopPropagation();
                onToggleFavorite(property.id);
              }}
            >
              {favorites.includes(property.id) ? 'Remove from Favorites' : 'Add to Favorites'}
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default PropertyList;


