import React, { useState } from 'react'; // Import React and hooks
import properties from '../data/properties.json'; // Import properties from JSON file

const searchImage = process.env.PUBLIC_URL + '/assets/Search-page-image.jpg';

// State variable 'criteria' to store the search criteria entered by the user
function SearchForm({ onSearch }) {
  const [criteria, setCriteria] = useState({
    type: 'any', // Property type, default set to "any"
    minPrice: '',
    maxPrice: '',
    minBedrooms: '',
    maxBedrooms: '',
    dateAfter: '',
    dateBefore: '', // To filter properties between two dates
    postcode: '',
  });

  //const [searchResults, setSearchResults] = useState([]); // State variable to store search results

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent the default form submission behavior
  
    const filteredProperties = properties.filter((property) => {
      if (criteria.type !== 'any' && property.type.toLowerCase() !== criteria.type) return false;
      if (criteria.minPrice && property.price < parseInt(criteria.minPrice)) return false;
      if (criteria.maxPrice && property.price > parseInt(criteria.maxPrice)) return false;
      if (criteria.minBedrooms && property.bedrooms < parseInt(criteria.minBedrooms)) return false;
      if (criteria.maxBedrooms && property.bedrooms > parseInt(criteria.maxBedrooms)) return false;
      if (
        criteria.dateAfter &&
        new Date(property.added.year, property.added.month - 1, property.added.day) < new Date(criteria.dateAfter)
      )
        return false;
      if (
        criteria.dateBefore &&
        new Date(property.added.year, property.added.month - 1, property.added.day) > new Date(criteria.dateBefore)
      )
        return false;
      if (criteria.postcode && !property.postcode.startsWith(criteria.postcode)) return false;
  
      return true;
    });
  
    // Update search results state
    setSearchResults(filteredProperties);
  
    // Optionally, pass the results to the parent component
    if (onSearch) {
      onSearch(filteredProperties);
    }
  };

  // Function to handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCriteria({
      ...criteria,
      [name]: value,
    });
  };


  // const handleChange = (e) => {
  //   const { name, value } = e.target;
  //   setCriteria((prev) => ({
  //     ...prev,
  //     [name]: value, // Dynamically update the specific field
  //   }));
  // };

  // Render the search form UI
  return (
    <div className="search-form-container">
      {/* Left Side Image */}
      <div className="left-image">
        <img src={searchImage} alt="Property Search" />
      </div>

      {/* Right Side Search Form */}
      <form className="search-form" onSubmit={handleSubmit}>
        <div className="search-form-grid">
          
          {/* Property Type Dropdown */}
          <div className="form-group">
            <label htmlFor="type">Property Type</label>
            <select id="type" name="type" value={criteria.type} onChange={handleChange}>
              <option value="any">Any</option>
              <option value="house">House</option>
              <option value="flat">Flat</option>
            </select>
          </div>

          {/* Minimum Price Input */}
          <div className="form-group">
            <label htmlFor="minPrice">Min Price (£)</label>
            <input
              type="number"
              id="minPrice"
              name="minPrice"
              value={criteria.minPrice}
              onChange={handleChange}
            />
          </div>

          {/* Maximum Price Input */}
          <div className="form-group">
            <label htmlFor="maxPrice">Max Price (£)</label>
            <input
              type="number"
              id="maxPrice"
              name="maxPrice"
              value={criteria.maxPrice}
              onChange={handleChange}
            />
          </div>

          {/* Minimum Bedrooms Dropdown */}
          <div className="form-group">
            <label htmlFor="minBedrooms">Min Bedrooms</label>
            <select
              id="minBedrooms"
              name="minBedrooms"
              value={criteria.minBedrooms}
              onChange={handleChange}
            >
              <option value="">Any</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
            </select>
          </div>

          {/* Maximum Bedrooms Dropdown */}
          <div className="form-group">
            <label htmlFor="maxBedrooms">Max Bedrooms</label>
            <select
              id="maxBedrooms"
              name="maxBedrooms"
              value={criteria.maxBedrooms}
              onChange={handleChange}
            >
              <option value="">Any</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
            </select>
          </div>

          {/* Date Added After Input */}
          <div className="form-group">
            <label htmlFor="dateAfter">Added After</label>
            <input
              type="date"
              id="dateAfter"
              name="dateAfter"
              value={criteria.dateAfter}
              onChange={handleChange}
            />
          </div>

          {/* Date Added Before Input */}
          <div className="form-group">
            <label htmlFor="dateBefore">Added Before</label>
            <input
              type="date"
              id="dateBefore"
              name="dateBefore"
              value={criteria.dateBefore}
              onChange={handleChange}
            />
          </div>

          {/* Postcode Dropdown */}
          <div className="form-group">
            <label htmlFor="postcode">Postcode Area</label>
            <select
              id="postcode"
              name="postcode"
              value={criteria.postcode}
              onChange={handleChange}
            >
              <option value="">Any</option>
              <option value="abc">ABC</option>
              <option value="ab">AB</option>
              <option value="c">C</option>
              <option value="cba">CBA</option>
              <option value="ba">BA</option>
            </select>
          </div>

          {/* Submit Button */}
          <div className="form-group search-button">
            <button type="submit" className="search-button">
              Search Properties
            </button>
          </div>
        </div>
      </form>  
    </div>        
  );
}

export default SearchForm;
