import React, { useState } from 'react';

export const PropertyDetails = ({
  property,
  onBack,
  isFavorite,
  onToggleFavorite,
}) => {
  const [activeTab, setActiveTab] = useState('description');
  const [selectedImage, setSelectedImage] = useState(0);
  const [isFullscreen, setIsFullscreen] = useState(false);

  if (!property) return null;

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-GB', {
      style: 'currency',
      currency: 'GBP',
      maximumFractionDigits: 0,
    }).format(price);
  };

   //image gallery controls
   const nextImage = () => {
    setSelectedImage((prev) => 
      prev === property.images.length - 1 ? 0 : prev + 1
    );
  };

  const previousImage = () => {
    setSelectedImage((prev) => 
      prev === 0 ? property.images.length - 1 : prev - 1
    );
  };

  //fullscreen toggle
  const toggleFullscreen = () => {
    setIsFullscreen(!isFullscreen);
  };

  return (
    <div className="property-details-page">
      <button onClick={onBack} className="back-button">
        ← Back to Search
      </button>

      <div className={`image-gallery ${isFullscreen ? 'fullscreen' : ''}`}>
        <button onClick={previousImage} className="gallery-nav prev">
          ‹
        </button>
        <button onClick={nextImage} className="gallery-nav next">
          ›
        </button>

        <img
          src={`${property.images[selectedImage]}`}
          alt={`${property.title} - view ${selectedImage + 1}`}
          className="main-image"
          onClick={toggleFullscreen}
        />

        <div className="thumbnails">
          {property.images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={`Thumbnail ${index + 1}`}
              className={`thumbnail ${selectedImage === index ? 'active' : ''}`}
              onClick={() => setSelectedImage(index)}
            />
          ))}
        </div>
      </div>

      <h1>{property.title}</h1>
      <p className="property-price">{formatPrice(property.price)}</p>

      {/* Added drag support for favorites */}
      <div 
        className="favorite-container"
        draggable
        onDragStart={(e) => {
          e.dataTransfer.setData('propertyId', property.id.toString());
        }}
      >

       <button 
          onClick={onToggleFavorite}
          className={`favorite-button ${isFavorite ? 'active' : ''}`}
        >
          {isFavorite ? '❤️ Remove from Favorites' : '🤍 Add to Favorites'}
        </button>
      </div>

      <div className="tabs">
        <div className="tab-list">
          <button
            className={`tab ${activeTab === 'description' ? 'active' : ''}`}
            onClick={() => setActiveTab('description')}
          >
            Description
          </button>

          <button
            className={`tab ${activeTab === 'floorplan' ? 'active' : ''}`}
            onClick={() => setActiveTab('floorplan')}
          >
            Floor Plan
          </button>

          <button
            className={`tab ${activeTab === 'map' ? 'active' : ''}`}
            onClick={() => setActiveTab('map')}
          >
            Map
          </button>
        </div>

        <div className="tab-content">
          {activeTab === 'description' && (
            <div className='description-tab'>
              <p>{property.longDescription}</p>
              <h3>Key Features</h3>
              <ul>
                <li>{property.bedrooms} bedrooms</li>
                <li>{property.type}</li>
                <li>Location: {property.postcode}</li>
                <li>Added: {new Date(property.dateAdded).toLocaleDateString()}</li>
              </ul>
            </div>
          )}
          {activeTab === 'floorplan' && (
            <div className="floor">
              <p>Floor plan coming soon...</p>
            </div>
          )}
          {activeTab === 'map' && (
            <div className="map-container">
              <iframe
                width="100%"
                height="100%"
                frameBorder="0"
                style={{ border: 0 }}
                src={`https://www.google.com/maps/embed?q=${property.location.lat},${property.location.lng}`}
                allowFullScreen
              ></iframe>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
