import React, { useState, useEffect } from 'react';
import Header from '../components/Header.js';
import SearchForm from '../components/SearchForm.js';
import PropertyList from '../components/PropertyList.js';
import Favorites from '../components/Favourites.js';
import propertiesData from '../public/properties.json';

function App() {
  const [properties, setProperties] = useState(propertiesData.properties);
  const [filteredProperties, setFilteredProperties] = useState(properties);
  const [showFavorites, setShowFavorites] = useState(false);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const savedFavorites = localStorage.getItem('favorites');
    if (savedFavorites) {
      setFavorites(JSON.parse(savedFavorites));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  const handleSearch = (criteria) => {
    let results = properties;

    if (criteria.type && criteria.type !== 'any') {
      results = results.filter(property => property.type === criteria.type);
    }

    if (criteria.minPrice) {
      results = results.filter(property => property.price >= criteria.minPrice);
    }

    if (criteria.maxPrice) {
      results = results.filter(property => property.price <= criteria.maxPrice);
    }

    if (criteria.minBedrooms) {
      results = results.filter(property => property.bedrooms >= criteria.minBedrooms);
    }

    if (criteria.maxBedrooms) {
      results = results.filter(property => property.bedrooms <= criteria.maxBedrooms);
    }

    if (criteria.dateAfter) {
      results = results.filter(property => 
        new Date(property.dateAdded) >= new Date(criteria.dateAfter)
      );
    }

    if (criteria.postcode) {
      results = results.filter(property => 
        property.postcode.toLowerCase().startsWith(criteria.postcode.toLowerCase())
      );
    }

    setFilteredProperties(results);
  };

  const toggleFavorite = (propertyId) => {
    setFavorites(prev => {
      if (prev.includes(propertyId)) {
        return prev.filter(id => id !== propertyId);
      }
      return [...prev, propertyId];
    });
  };

  const favoriteProperties = properties.filter(property => 
    favorites.includes(property.id)
  );

  return (
    <div>
      <Header />
      <button 
        className="favorites-toggle"
        onClick={() => setShowFavorites(!showFavorites)}
      >
        {showFavorites ? 'Hide Favorites' : 'Show Favorites'}
      </button>
      <SearchForm onSearch={handleSearch} />
      <PropertyList
        properties={filteredProperties}
        onPropertySelect={() => {}}
        favorites={favorites}
        onToggleFavorite={toggleFavorite}
      />
      <Favorites
        show={showFavorites}
        properties={favoriteProperties}
        onPropertySelect={() => {}}
        onToggleFavorite={toggleFavorite}
      />
    </div>
  );
}

export default App;
