import React from 'react';

function PropertyList({ properties, onPropertySelect, favorites, onToggleFavorite }) {
  if (properties.length === 0) {
    return <div>No properties match your criteria.</div>;
  }

  return (
    <div className="property-list">
      {properties.map((property) => (
        <div key={property.id} className="property-item">
          <h2>{property.title}</h2>
          <p>{property.shortDescription}</p>
          <p><strong>Price:</strong> ¬£{property.price}</p>
          <p><strong>Bedrooms:</strong> {property.bedrooms}</p>
          <p><strong>Location:</strong> {property.location}</p>
          <button onClick={() => onPropertySelect(property)}>View Details</button>
          <button onClick={() => onToggleFavorite(property.id)}>
            {favorites.includes(property.id) ? 'Remove from Favorites' : 'Add to Favorites'}
          </button>
        </div>
      ))}
    </div>
  );
}

export default PropertyList;




