import React from 'react';
import Header from './components/Header'; // Import Header component
import Footer from './components/Footer'; // Import Footer component
import SearchForm from './components/SearchForm'; // Import SearchForm component
import PropertyList from './components/PropertyList'; // Import PropertyList component
import Favorites from './components/Favourites'; // Import Favorites component
import PropertyDetails from './components/PropertyDetails'; // Import PropertyDetails component
import propertiesData from './data/properties.json';

const properties = propertiesData.properties;
// Home component manages the layout, conditional rendering, and interaction
function Home({
  selectedProperty,
  showFavorites,
  setShowFavorites,
  updateFilteredProperties,
  searchPerformed,
  filteredProperties,
  handlePropertySelect,
  toggleFavorite,
  favorites,
  properties,
  handleBackToSearch
}) {
  return (
    <div>
      <Header />
      {/* Render either property search or details page based on selected property */}
      {!selectedProperty ? (
        <>
          {/* Button to toggle showing of favorites */}
          <button 
            className="favorites-toggle"
            onClick={() => setShowFavorites(!showFavorites)}
          >
            {showFavorites ? 'X' : 'Show Favorites'}
          </button>

          {/* Search form to filter properties */}
          <SearchForm onSearch={updateFilteredProperties} />

          {/* Conditional message for search results */}
          {!searchPerformed ? (
            <p className="search-results-message">All properties:</p>
          ) 
          : filteredProperties.length > 0 ? (
            <p className="search-results-message">Here are the search results:</p>
          ) : (
            <p className="no-results-message">No properties found. Please try different criteria.</p>
          )}

          {/* List of filtered properties */}
          <PropertyList
            properties={filteredProperties}
            onPropertySelect={handlePropertySelect}
            favorites={favorites}
            onToggleFavorite={toggleFavorite}
          />

          {/* Favorites section, conditionally rendered */}
          <Favorites
            show={showFavorites}
            properties={properties.filter(property => favorites.includes(property.id))}
            onPropertySelect={handlePropertySelect}
            onToggleFavorite={toggleFavorite}
          />
        </>
      ) : (
        <PropertyDetails
          property={selectedProperty}
          onBack={handleBackToSearch}
          isFavorite={favorites.includes(selectedProperty.id)}
          onToggleFavorite={() => toggleFavorite(selectedProperty.id)}
        />
      )}
      <Footer />
    </div>
  );
}

export default Home;
