import React, { useState } from 'react'; // Import React and useState for local state management

function Favorites({ show, properties, onToggleFavorite }) {
  const [favorites, setFavorites] = useState([]); // Initialize favorites state
  const [showClearConfirmation, setShowClearConfirmation] = useState(false);

  // Handles the drag over event to allow dropping an item
  const handleDragOver = (e) => {
    e.preventDefault(); // Prevent default behavior to allow drop
    const target = e.target;
    if (target.classList.contains('droppable')) {
      target.classList.add('active'); // Add active class to highlight the droppable area
    }
  };

  // Handles the drag leave event to remove the highlight from the droppable area
  const handleDragLeave = (e) => {
    const target = e.target;
    if (target.classList.contains('droppable')) {
      target.classList.remove('active'); // Remove active class when the dragged item leaves the area
    }
  };

  // Handles the drop event to add a property to favorites
  const handleDrop = (e) => {
    e.preventDefault(); // Prevent default behavior of the drop event
    const propertyId = parseInt(e.dataTransfer.getData('propertyId'), 10); // Retrieve the property ID from the drag event
    toggleFavorite(propertyId); // Toggle favorite status for the dropped property
  };

  // Function to toggle favorite status
  const toggleFavorite = (propertyId) => {
    setFavorites(prev => {
      if (prev.includes(propertyId)) {
        return prev.filter(id => id !== propertyId); // Remove from favorites
      }
      return [...prev, propertyId]; // Add to favorites
    });
  };

  // Formats the price into GBP currency format
  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-GB', {
      style: 'currency',
      currency: 'GBP',
      maximumFractionDigits: 0 // No decimal points
    }).format(price); // Format the price as a currency string
  };

  const handleClearFavorites = () => {
    // Show confirmation before clearing favorites
    setShowClearConfirmation(true);
  };

  const confirmClearFavorites = () => {
    // Clear favorites and close confirmation
    onClearFavorites();
    setShowClearConfirmation(false);
  };

  const cancelClearFavorites = () => {
    // Cancel clearing favorites and close confirmation
    setShowClearConfirmation(false);
  };


  return (

    <div className={`favorites ${show ? 'open' : ''}`}>
      <h2>Favorite Properties</h2>
      {showClearConfirmation && (
        <div className="clear-confirmation">
          <p>Are you sure you want to clear all favorites?</p>
          <button onClick={confirmClearFavorites}>Yes</button>
          <button onClick={cancelClearFavorites}>No</button>
        </div>
      )}

      <div
        className="droppable"
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        {properties.length === 0 ? (
          <p>Drag properties here to add to favorites</p>
        ) : (
          properties.map(property => (
            <div key={property.id} className="favorite-item">
              <img
                src={`${property.picture}`}
                alt={property.title}
                className="favorite-item-img"
              />
              <div>
                <h4 className='name-in-fav'>{property.title}</h4>
                <p className='price-in-fav'>{formatPrice(property.price)}</p>
                <button> View Details</button>
                {/* <button onClick={() => toggleFavorite(property.id)}>
                  {favorites.includes(property.id) ? 'Remove' : 'View '}
                </button> */}

                <button>Remove</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Favorites;
