import React, { useState } from 'react'; // Import React and hooks

// State variable 'criteria' to store the search criteria entered by the user
function SearchForm({ onSearch }) {
  const [criteria, setCriteria] = useState({
    type: 'any', // Property type, default set to "any"
    minPrice: '',
    maxPrice: '',
    minBedrooms: '',
    maxBedrooms: '',
    dateAfter: '',
    dateBefore: '', // To filter properties between two dates
    postcode: '',
  });

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent the default behavior of form submission
    onSearch(criteria); // Trigger the parent function with criteria
  };

  // Function to handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCriteria((prev) => ({
      ...prev,
      [name]: value, // Dynamically update the specific field
    }));
  };

  // Render the search form UI
  return (
    <div className="search-form-container">
      {/* Left Side Image */}
      <div className="left-image">
        <img src="/Search-page-image.jpg" alt="Property Search" />
      </div>

      {/* Right Side Search Form */}
      <form className="search-form" onSubmit={handleSubmit}>
        <div className="search-form-grid">
          
          {/* Property Type Dropdown */}
          <div className="form-group">
            <label htmlFor="type">Property Type</label>
            <select id="type" name="type" value={criteria.type} onChange={handleChange}>
              <option value="any">Any</option>
              <option value="house">House</option>
              <option value="flat">Flat</option>
            </select>
          </div>

          {/* Minimum Price Input */}
          <div className="form-group">
            <label htmlFor="minPrice">Min Price (£)</label>
            <input
              type="number"
              id="minPrice"
              name="minPrice"
              value={criteria.minPrice}
              onChange={handleChange}
            />
          </div>

          {/* Maximum Price Input */}
          <div className="form-group">
            <label htmlFor="maxPrice">Max Price (£)</label>
            <input
              type="number"
              id="maxPrice"
              name="maxPrice"
              value={criteria.maxPrice}
              onChange={handleChange}
            />
          </div>

          {/* Minimum Bedrooms Dropdown */}
          <div className="form-group">
            <label htmlFor="minBedrooms">Min Bedrooms</label>
            <select
              id="minBedrooms"
              name="minBedrooms"
              value={criteria.minBedrooms}
              onChange={handleChange}
            >
              <option value="">Any</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
            </select>
          </div>

          {/* Maximum Bedrooms Dropdown */}
          <div className="form-group">
            <label htmlFor="maxBedrooms">Max Bedrooms</label>
            <select
              id="maxBedrooms"
              name="maxBedrooms"
              value={criteria.maxBedrooms}
              onChange={handleChange}
            >
              <option value="">Any</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
            </select>
          </div>

          {/* Date Added After Input */}
          <div className="form-group">
            <label htmlFor="dateAfter">Added After</label>
            <input
              type="date"
              id="dateAfter"
              name="dateAfter"
              value={criteria.dateAfter}
              onChange={handleChange}
            />
          </div>

          {/* Date Added Before Input */}
          <div className="form-group">
            <label htmlFor="dateBefore">Added Before</label>
            <input
              type="date"
              id="dateBefore"
              name="dateBefore"
              value={criteria.dateBefore}
              onChange={handleChange}
            />
          </div>

          {/* Postcode Dropdown */}
          <div className="form-group">
            <label htmlFor="postcode">Postcode Area</label>
            <select
              id="postcode"
              name="postcode"
              value={criteria.postcode}
              onChange={handleChange}
            >
              <option value="">Any</option>
              <option value="abc">ABC</option>
              <option value="ab">AB</option>
              <option value="c">C</option>
              <option value="cba">CBA</option>
              <option value="ba">BA</option>
            </select>
          </div>

          {/* Submit Button */}
          <div className="form-group search-button">
            <button type="submit" className="search-button">
              Search Properties
            </button>
          </div>
        </div>
      </form>  
    </div>        
  );
}

export default SearchForm;
