import React, { useState, useEffect } from 'react';

const Favourites = ({ children }) => {
  const [favorites, setFavorites] = useState([]);
  const [showFavorites, setShowFavorites] = useState(false); // State to manage the visibility of the favorites container
  const [showClearConfirmation, setShowClearConfirmation] = useState(false);

  // Load favorites from local storage
  useEffect(() => {
    const savedFavorites = localStorage.getItem('favorites');
    if (savedFavorites) {
      setFavorites(JSON.parse(savedFavorites));
    }
  }, []);

  // Save favorites to local storage whenever it changes
  useEffect(() => {
    localStorage.setItem('favorites', JSON.stringify(favorites));
  }, [favorites]);

  const toggleFavorite = (propertyId) => {
    setFavorites((prevFavorites) => {
      if (prevFavorites.includes(propertyId)) {
        return prevFavorites.filter(id => id !== propertyId); // Remove from favorites
      } else {
        return [...prevFavorites, propertyId]; // Add to favorites
      }
    });
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    const target = e.target;
    if (target.classList.contains('droppable')) {
      target.classList.add('active');
    }
  };

  const handleDragLeave = (e) => {
    const target = e.target;
    if (target.classList.contains('droppable')) {
      target.classList.remove('active');
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const propertyId = parseInt(e.dataTransfer.getData('propertyId'), 10);
    toggleFavorite(propertyId); // Toggle favorite status on drop
  };

  const handleClearFavorites = () => {
    setShowClearConfirmation(true); // Show confirmation before clearing favorites
  };

  const clearFavorites = () => {
    setFavorites([]);
    localStorage.removeItem('favorites');
  };

  const confirmClearFavorites = () => {
    clearFavorites();
    setShowClearConfirmation(false); // Close confirmation
  };

  const cancelClearFavorites = () => {
    setShowClearConfirmation(false); // Cancel clearing favorites
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-GB', {
      style: 'currency',
      currency: 'GBP',
      maximumFractionDigits: 0
    }).format(price); // Format price in GBP
  };

  return (
    <div>
      <button 
        className="favorites-toggle" 
        onClick={() => setShowFavorites(!showFavorites)}
      >
        {showFavorites ? 'Hide Favourites' : 'Show Favourites'}
      </button>
      {showFavorites && (
        <div className="favorites-container">
          <h2>Favorite Properties</h2>
          {showClearConfirmation && (
            <div className="clear-confirmation">
              <p>Are you sure you want to clear all favorites?</p>
              <button onClick={confirmClearFavorites}>Yes</button>
              <button onClick={cancelClearFavorites}>No</button>
            </div>
          )}
          <div
            className="droppable"
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
          >
            {favorites.length === 0 ? (
              <p>Drag properties here to add to favorites</p>
            ) : (
              favorites.map(favId => {
                const property = children({ favorites, toggleFavorite }).properties.find(p => p.id === favId);
                return (
                  <div key={property.id} className="favorite-item">
                    <img src={property.picture} alt={property.title} className="favorite-item-img" />
                    <div>
                      <h4 className='name-in-fav'>{property.title}</h4>
                      <p className='price-in-fav'>{formatPrice(property.price)}</p>
                      <button onClick={() => toggleFavorite(property.id)}>Remove</button>
                    </div>
                  </div>
                );
              })
            )}
          </div>
          <button onClick={handleClearFavorites} className="clear-fav-btn">Clear</button>
        </div>
      )}
      {children({ favorites, toggleFavorite })}
    </div>
  );
};

export default Favourites;
