import React from 'react';

function Favorites({ show, properties, onToggleFavorite }) {
  const handleDragOver = (e) => {
    e.preventDefault();
    const target = e.target;
    // if (target.classList.contains('droppable')) {
    //   target.classList.add('active');
    // }
  };

  const handleDragLeave = (e) => {
    const target = e.target;
    if (target.classList.contains('droppable')) {
      target.classList.remove('active');
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const propertyId = parseInt(e.dataTransfer.getData('propertyId'), 10);
    onToggleFavorite(propertyId);
    const target = e.target;
    if (target.classList.contains('droppable')) {
      target.classList.remove('active');
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-GB', {
      style: 'currency',
      currency: 'GBP',
      maximumFractionDigits: 0
    }).format(price);
  };

  return (
    <div className={`favorites ${show ? 'open' : ''}`}>
      <h2>Favorite Properties</h2>
      <div
        className="droppable"
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        {properties.length === 0 ? (
          <p>Drag properties here to add to favorites</p>
        ) : (
          properties.map(property => (
            <div key={property.id} className="favorite-item">
              <img
                src={`${property.picture}`}
                alt={property.title}
              />
              <div>
                <h3>{property.title}</h3>
                <p>{formatPrice(property.price)}</p>
                <button onClick={() => onToggleFavorite(property.id)}>
                  Remove
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Favorites;
