import React from 'react';

const Favorites = ({ show, properties, onPropertySelect, onToggleFavorite }) => {
  return (
    <div className={`favorites ${show ? 'open' : ''}`}>
      <button onClick={() => onToggleFavorite('')}>X</button>
      <h3>Your Favorites</h3>
      <ul className="favorites-list">
        {properties.map((property) => (
          <li
            key={property.id}
            onClick={() => onPropertySelect(property)}
          >
            <img
              src={property.imageUrl}
              alt={property.title}
            />
            <span>{property.title}</span>
            <button
              onClick={(e) => {
                e.stopPropagation();
                onToggleFavorite(property.id);
              }}
            >
              Remove
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Favorites;


